mapper.update:
UPDATE BLOG
SET LIKE_COUNT = LIKE_COUNT + #{value}
WHERE BLOG_ID = #{blogId}

mapper.select:
SELECT 	LIKE_COUNT
FROM	BLOG
WHERE	BLOG_ID = #{blogId}

